    "800607": {
        "Desc": "Besiege den Abgrundboten.",
        "Talk": []
    },
    "800608": {
        "Desc": "Sprich mit Dainsleif.",
        "Talk": [
            [
                {
                    "N": "_1005",
                    "T": "Puh! Der Abgrundbote, den du so lange gejagt hast, ist endlich ausgeschaltet!"
                },
                {
                    "N": "Dainsleif",
                    "T": "Es ist keine Zeit für müßiges Geschwätz. Es scheint, dass die Vorrichtung vom Orden des Abgrunds aktiviert wird und es noch nicht zu spät ist, sie zu zerstören."
                },
                {
                    "N": "_1005",
                    "T": "Ja! Lasst uns hingehen und es uns ansehen!"
                }
            ]
        ]
    },
    "800701": {
        "Desc": "Geh in den Raum in der Mitte der obersten Etage der Ruine und sieh nach, was los ist.",
        "Talk": [
            [
                {
                    "N": "Dainsleif",
                    "T": "Ich dachte, Halfdans Seele würde sich im Nu in Luft auflösen ... Es ist erstaunlich, wie stark seine Seele ist."
                },
                {
                    "N": "Abgrundpriester",
                    "T": "Du stehst im Weg! Geh aus ..."
                },
                {
                    "N": "Dainsleif",
                    "T": "Wo schaust du hin? Eine seltene Gelegenheit für dich, mich endlich loszuwerden, nicht wahr?"
                },
                {
                    "N": "Abgrundpriester",
                    "T": "Dainsleif!"
                },
                {
                    "N": "Dainsleif",
                    "T": "Macht ihn fertig und schaltet den Apparat ab!"
                }
            ]
        ]
    },
