    "1400607": {
        "Desc": "Geh zum Produktionstrakt.",
        "Talk": [
            [
                {
                    "N": "_1005",
                    "T": "Ist dieser Mann hierhergeflohen?"
                },
                {
                    "N": "Wriothesley",
                    "T": "Sehr wahrscheinlich. Er hat vermutlich bereits Nachrichten bekommen und ist geflohen, um Dougiers Schutz zu suchen."
                },
                {
                    "N": "Wriothesley",
                    "T": "Gehen wir weiter nach unten. Es gibt dort einige verlassene Gebiete. Da er Platz brauchte, hat Dougier diesen Ort wahrscheinlich benutzt, um diese Mitglieder festzuhalten."
                },
                {
                    "N": "Wriothesley",
                    "T": "Wir sollten auf dem richtigen Weg sein. Jetzt müssen wir nur noch diesen Wachmann finden."
                },
                {
                    "N": "Wriothesley",
                    "T": "Lasst uns loslegen. Wir gehen diesen Weg, ihr geht den anderen."
                }
            ]
        ]
    },
    "1400608": {
        "Desc": "Untersuche mit Wriothesley den Produktionstrakt.",
        "Talk": []
    },
    "1400609": {
        "Desc": "Fang die geflohenen Mitglieder der Barettgesellschaft ein.",
        "Talk": [
            [
                {
                    "N": "Wyler",
                    "T": "Du verfluchter Wächter!"
                },
                {
                    "N": "Gouzien",
                    "T": "*keuch* ... *keuch* ..."
                },
                {
                    "N": "Wriothesley",
                    "T": "Hat Dougier euch geschickt? Warum habt ihr diesen Wachmann angegriffen?"
                },
                {
                    "N": "Wyler",
                    "T": "..."
                },
                {
                    "N": "_1005",
                    "T": "Schnell, raus mit den Worten! Merkst du nicht, dass wir euch helfen wollen?!"
                },
                {
                    "N": "Wriothesley",
                    "T": "Bei der Verhandlung kann ich eure Mitarbeit berücksichtigen."
                },
                {
                    "N": "Wyler",
                    "T": "Der Leiter ... Er ... Er hatte Angst, dass dieser Typ unseren wahren Stützpunkt verraten würde, wenn er diesen Ort finden würde ..."
                },
                {
                    "N": "Wyler",
                    "T": "Eigentlich wollten wir ihn direkt abwerfen, aber ihr habt uns schon hierher gejagt."
                },
                {
                    "N": "Wriothesley",
                    "T": "Mit anderen Worten, euer wahrer Stützpunkt liegt in dieser Richtung, ja?"
                },
                {
                    "N": "Gouzien",
                    "T": "J... Ja. Geht diesen Weg entlang und ihr werdet ein Abflussrohr sehen. Geht dann weiter hinein und ihr werdet schon dort ankommen."
                },
                {
                    "N": "_0",
                    "T": "So ist das vernünftig."
                },
                {
                    "N": "Wriothesley",
                    "T": "Wachen, bringt sie weg."
                },
                {
                    "N": "Wriothesley",
                    "T": "Gehen wir. Es ist an der Zeit, herauszufinden, was Dougier wirklich vorhat."
                }
            ]
        ]
    },
